var tempX = 0
var tempY = 0
class Map extends React.Component {
    constructor(props) {
        super(props);
        this.state = {canvas: null,ctx : null,list:[],dict : {},cnt :0,todos : {}};
    }    
    componentDidMount() {
        db.ref('/users/name').on('value', querySnapShot => {
            let data = querySnapShot.val() ? querySnapShot.val() : {};
            let todoItems = data;
