            var start_y = y;   
            for(var j=0;j<2;j++){
                for(var i=0;i<4;i++){
                    var final_x = start_x + size;
                    var final_y = start_y + size;
                    ctx.strokeRect(start_x,start_y,size,size);
                    start_x = start_x + size;
                }
                start_x = x;
                start_y = start_y + size;
            }
        }
        var x = 150
