                    ctx.strokeRect(start_x,start_y,size,size);
                    start_x = start_x + size;
                }
                start_x = x;
                start_y = start_y + size;
            }
        }
        var x = 730
        var y = 30
        var n = 4
        var length = 580
        var gap = 280     
        for(var i=0;i<n;i++){
            ctx.beginPath();
            ctx.moveTo(x,y);
            ctx.lineTo(x+length,y);
            y = y + gap;
            ctx.strokeStyle = "#FF0000";
            ctx.stroke();
        }        
        ctx.beginPath();
        ctx.moveTo(730,30);
        ctx.lineTo(730,30+280*3);
        ctx.strokeStyle = "#FF0000";
        ctx.stroke();
        ctx.beginPath();
        ctx.moveTo(730+580,30);
        ctx.lineTo(730+580,30+280*3);
        ctx.strokeStyle = "#FF0000";
        ctx.stroke();
        x = 0
        y = 170
        size = 100
        for(var i=0;i<3;i++){
            ctx.moveTo(x,y);
            ctx.strokeRect(x,y,size+20,size);
            y = y + 200;
        }
        ctx.strokeStyle = "#000000"
        ctx.strokeRect(5,750,100,100)
        mouseX = 150;
        mouseY = 870;
        this.setState({
            ctx : ctx
        });
    }
    deleteArc(){
        this.state.ctx.beginPath()
        this.state.ctx.arc(tempX, tempY, 20, 0, 2 * Math.PI);
        this.state.ctx.stroke();
    }
    update(x,y){
        this.state.ctx.beginPath()
        this.state.ctx.arc(x, y, 20, 0, 2 * Math.PI);
        this.state.ctx.stroke();
        tempX = x
        tempY = y
    }
    addToUsers() {
        db.ref('/users').push({
            name : "Dhruv"
        });
    }
    addToShelfs(x,y){
        list2.push([x,y])
        db.ref('/shelfs').push({
            shelf_num : cnt,
            coordinates : [x,y],
            connected_coordinates : list2 
        })
        cnt += 1
    }
    removeFromShelfs(){
        db.ref('/shelfs').remove()
    }
    addToList(x,y){
        list.push([x,y]);
        this.setState({
            list : list
        })
        console.log(list);
    }
    addToDict(x,x1,y1){
        dict1["shelf" + x] = [x1,y1]
        this.setState({
            dict : dict1
        })
        console.log(dict1);
        // const cnt1 = x + 1
